import React, { useState } from 'react';
import './Carousel.css';

const Carousel = ({items}) => {
    const [currentItem, setCurrentItem] = useState(0);

    const nextItem = () => {
      setCurrentItem((currentItem + 1) % items.length);
    };

    const prevItem = () => {
      setCurrentItem((currentItem - 1 + items.length) % items.length);
    };

    return (
        <div className="carousel-container">
        <div className="swiper-wrapper">
          {items.map((item, index) => (
            <div
              key={index}
              className={`swiper-slide ${index === currentItem ? 'active' : ''}`}
              style={{ transform: `translateX(${100 * (index - currentItem)}%)` }}
            >
              <img src={item} alt={`Item ${index}`} />
            </div>
          ))}
        </div>
        <button className="swiper-button-prev" onClick={prevItem}>Précédent</button>
        <button className="swiper-button-next" onClick={nextItem}>Suivant</button>
      </div>
    );
  };

  export default Carousel;
