import React, { useState } from 'react';
import './Carousel.css';

const Carousel = ({ items }) => {
  const [currentItem, setCurrentItem] = useState(0);

  const nextItem = () => {
    setCurrentItem((currentItem + 1) % items.length);
  };

  const prevItem = () => {
    setCurrentItem((currentItem - 1 + items.length) % items.length);
  };

  if (items.length === 0) {
    return <div className="carousel-container">Aucun élément à afficher.</div>;
  }

  return (
    <div className="carousel-container">
      <div className="carousel-arrow carousel-prev-arrow" onClick={prevItem}></div>
      <div className="carousel-item">
        <img src={items[currentItem]} alt={`Item ${currentItem}`} />
      </div>
      <div className="carousel-arrow carousel-next-arrow" onClick={nextItem}></div>
    </div>
  );
};

export default Carousel;
