import React, { useState } from 'react';
import './Carousel.css'; // Assurez-vous de créer un fichier CSS correspondant

const Carousel = ({ items }) => {
  const [currentItem, setCurrentItem] = useState(0);

  const nextItem = () => {
    setCurrentItem((currentItem + 1) % items.length);
  };

  const prevItem = () => {
    setCurrentItem((currentItem - 1 + items.length) % items.length);
  };

  return (
    <div className="carousel-container">
      <button onClick={prevItem} className="carousel-button">Précédent</button>
      <div className="carousel-item">
        <img src={items[currentItem]} alt={`Item ${currentItem}`} />
      </div>
      <button onClick={nextItem} className="carousel-button">Suivant</button>
    </div>
  );
};

export default Carousel;
