import React, { useState, useEffect } from 'react';
import './ProgressBar.css';

const ProgressBar = ({ percentage, duration }) => {
  const [width, setWidth] = useState(percentage || 0);

  useEffect(() => {
    const updateWidth = () => {
      setWidth(percentage);
    };

    const animationDuration = duration || 1000; // Durée de l'animation en millisecondes

    const animationInterval = setInterval(updateWidth, animationDuration / 100);

    return () => clearInterval(animationInterval);
  }, [percentage, duration]);

  return (
    <div className="progress-bar-container">
      <div className="progress-bar" style={{ width: `${width}%` }}>
        <div className="percentage-text">{width}%</div>
      </div>
    </div>
  );
};

export default ProgressBar;
