import React, { useRef, useState } from 'react';
import './Carousel.css';

const getCircularItem = (array, index) => {
    const length = array.length;
    if (length === 0) return null;
    return array[(index % length + length) % length];
  };

  const Carousel = ({items}) => {
    const [currentItem, setCurrentItem] = useState(0);
    const prevButtonRef = useRef();

    const nextItem = () => {
        const timer = setTimeout(() => {setCurrentItem(currentItem + 1)}, 1000);
        return () => clearTimeout(timer);
      };

    const prevItem = () => {
      const timer = setTimeout(() => {setCurrentItem(currentItem - 1)}, 1000);
      return () => clearTimeout(timer);
    };

    return (
      <div className="carousel-container">
        <div className="swiper-wrapper">
        <div className="swiper-slide">
            <img src={getCircularItem(items, currentItem - 2)} alt={`Previous Item`} />
          </div>
          <div className="swiper-slide">
            <img src={getCircularItem(items, currentItem - 1)} alt={`Previous Item`} />
          </div>
          <div className="swiper-slide">
            <img src={getCircularItem(items, currentItem)} alt={`Current Item`} />
          </div>
          <div className="swiper-slide">
            <img src={getCircularItem(items, currentItem + 1)} alt={`Next Item`} />
          </div>
          <div className="swiper-slide">
            <img src={getCircularItem(items, currentItem + 2)} alt={`Next Item`} />
          </div>
        </div>
        <button ref={prevButtonRef} className="swiper-button-prev" onClick={prevItem}>Précédent</button>
        <button className="swiper-button-next" onClick={nextItem}>Suivant</button>
      </div>
    );
  };

  export default Carousel;
