import React, { useState } from 'react';
import './Carousel.css';

const Carousel = ({ items }) => {
  const [currentItem, setCurrentItem] = useState(0);
  const [slideDirection, setSlideDirection] = useState(null);

  const nextItem = () => {
    setSlideDirection('next');
    setTimeout(() => {
      setCurrentItem((currentItem + 1) % items.length);
      setSlideDirection(null);
    }, 500); // Délai correspondant à la durée de la transition en millisecondes
  };

  const prevItem = () => {
    setSlideDirection('prev');
    setTimeout(() => {
      setCurrentItem((currentItem - 1 + items.length) % items.length);
      setSlideDirection(null);
    }, 500); // Délai correspondant à la durée de la transition en millisecondes
  };

  if (items.length === 0) {
    return <div className="carousel-container">Aucun élément à afficher.</div>;
  }

  return (
    <div className="carousel-container">
      <div className={`carousel-preview carousel-preview-prev ${slideDirection === 'prev' ? 'hidden' : ''}`} onClick={prevItem}>
        <img src={items[(currentItem - 1 + items.length) % items.length]} alt={`Item ${currentItem - 1}`} />
      </div>
      <div className={`carousel-item ${slideDirection === 'prev' ? 'slide-out' : slideDirection === 'next' ? 'slide-in' : ''}`}>
        <img src={items[currentItem]} alt={`Item ${currentItem}`} />
      </div>
      <div className={`carousel-preview carousel-preview-next ${slideDirection === 'next' ? 'hidden' : ''}`} onClick={nextItem}>
        <img src={items[(currentItem + 1) % items.length]} alt={`Item ${currentItem + 1}`} />
      </div>
    </div>
  );
};

export default Carousel;
