import React, { useState } from 'react';
import './Carousel.css';

const Carousel = ({ items }) => {
  const [currentItem, setCurrentItem] = useState(0);

  const nextItem = () => {
    setCurrentItem((currentItem + 1) % items.length);
  };

  const prevItem = () => {
    setCurrentItem((currentItem - 1 + items.length) % items.length);
  };

  if (items.length === 0) {
    return <div className="carousel-container">Aucun élément à afficher.</div>;
  }

  return (
    <div className="carousel-container">
      {items.length > 1 && (
        <button onClick={prevItem} className="carousel-button carousel-prev-button">
          Précédent
        </button>
      )}
      <div className="carousel-item">
        <img src={items[currentItem]} alt={`Item ${currentItem}`} />
      </div>
      {items.length > 1 && (
        <button onClick={nextItem} className="carousel-button carousel-next-button">
          Suivant
        </button>
      )}
    </div>
  );
};

export default Carousel;
