import React, { useState } from 'react';
import './Carousel.css';

const Carousel = ({items}) => {
    const [currentItem, setCurrentItem] = useState(0);

    const nextItem = () => {
      setCurrentItem((currentItem + 1) % items.length);
    };

    const prevItem = () => {
      setCurrentItem((currentItem - 1 + items.length) % items.length);
    };

    return (
      <div className="carousel-container">
        <div className="carousel-item">
          <img src={items[currentItem]} alt={`Item ${currentItem}`} />
        </div>
        <div className="carousel-controls">
          <button onClick={prevItem}>Précédent</button>
          <button onClick={nextItem}>Suivant</button>
        </div>
      </div>
    );
  };

  export default Carousel;
