import React, { useState } from 'react';
import './Carousel.css';

const Carousel = ({ items }) => {
    const [currentItem, setCurrentItem] = useState(0);
    const [isTransitioning, setIsTransitioning] = useState(false);

    const nextItem = () => {
      setIsTransitioning(true);
      setTimeout(() => {
        setCurrentItem((currentItem + 1) % items.length);
        setIsTransitioning(false);
      }, 500); // Délai correspondant à la durée de la transition en millisecondes
    };

    const prevItem = () => {
      setIsTransitioning(true);
      setTimeout(() => {
        setCurrentItem((currentItem - 1 + items.length) % items.length);
        setIsTransitioning(false);
      }, 500); // Délai correspondant à la durée de la transition en millisecondes
    };

    const transformStyle = {
      transform: `translateX(-${currentItem * 100}%)`,
    };

    return (
      <div className="carousel-container">
        <div className={`carousel-preview carousel-preview-prev ${isTransitioning ? 'hidden' : ''}`} onClick={prevItem}>
          <img src={items[(currentItem - 1 + items.length) % items.length]} alt={`Item ${currentItem - 1}`} />
        </div>
        <div className={`carousel-item ${isTransitioning ? 'slide-out' : ''}`} style={transformStyle}>
          {items.map((item, index) => (
            <img key={index} src={item} alt={`Item ${index}`} />
          ))}
        </div>
        <div className={`carousel-preview carousel-preview-next ${isTransitioning ? 'hidden' : ''}`} onClick={nextItem}>
          <img src={items[(currentItem + 1) % items.length]} alt={`Item ${currentItem + 1}`} />
        </div>
      </div>
    );
  };

  export default Carousel;
